import Foundation

import SwiftUI

struct PersonagemView: View {
    
//    @StateObject var viewModel = ViewModel()
        
    @State var image: String =  "data:image/jpeg;base64,"
    
    var desconhecido = "https://st3.depositphotos.com/3581215/18899/v/450/depositphotos_188994514-stock-illustration-vector-illustration-male-silhouette-profile.jpg"
    
    
    @State var name: String = "Talles Roberto"
    
    var body: some View {
        
        
        HStack{
            AsyncImage(url: URL(string: image == "Desconhecido" ? desconhecido : image)) { image in
                image
                    .resizable()
                    .clipShape(/*@START_MENU_TOKEN@*/Circle()/*@END_MENU_TOKEN@*/)
                    .frame(width: 100, height: 100)
                    .padding(.trailing, 20)
            }
            placeholder: {
                Image("Desconhecido")
                    .resizable()
                    .clipShape(/*@START_MENU_TOKEN@*/Circle()/*@END_MENU_TOKEN@*/)
                    .frame(width: 100, height: 100)
                    .padding(.trailing, 20)
            }
            
            Text(name)
                .font(.title3)
                .foregroundColor(.white)
            Spacer()
        }
        .padding(.leading, 20)
    }
}

